$(document).ready(function () {
  $(".js-open-menu").on("click", function (e) {
    e.preventDefault();
    openMenu();
  });

  $(".js-close-menu").on("click", function (e) {
    e.preventDefault();
    closeMenu();
  });

  function openMenu() {
    $(".header-nav").addClass("open");
  }

  function closeMenu() {
    $(".header-nav").removeClass("open");
  }
});
